package it11a;

public class Ex25 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float wert1, wert2;
		double mod;
		double div;

		wert1 = 5.0f;
		wert2 = 2.0f;

		mod = wert1 % wert2;
		div = wert1 / wert2;

		System.out.println("-------------------------------------------------");
		System.out.println("Berechne MODULO: \t" + wert1 + " % " + wert2 + " = " + mod);
		System.out.println("Berechne DIVISION: \t" + wert1 + " / " + wert2 + " = " + div);
		System.out.println("-------------------------------------------------");

		wert1 = 6.0f;
		wert2 = 3.0f;

		mod = wert1 % wert2;
		div = wert1 / wert2;

		System.out.println("Berechne MODULO: \t" + wert1 + " % " + wert2 + " = " + mod);
		System.out.println("Berechne DIVISION: \t" + wert1 + " / " + wert2 + " = " + div);
		System.out.println("-------------------------------------------------");
		wert1 = 7.0f;
		wert2 = 4.0f;

		mod = wert1 % wert2;
		div = wert1 / wert2;

		System.out.println("Berechne MODULO: \t" + wert1 + " % " + wert2 + " = " + mod);
		System.out.println("Berechne DIVISION: \t" + wert1 + " / " + wert2 + " = " + div);
		System.out.println("-------------------------------------------------");
	}

}
